export function t(key,params={}){const i18n=window.appI18n||{},language=i18n.language||document.documentElement.lang||"pl";let text=(i18n.translations&&i18n.translations[language]||{})[key]||key;return params&&"string"==typeof text&&Object.keys(params).forEach(param=>{const regex=new RegExp(`\\{\\{${param}\\}\\}`,"g");text=text.replace(regex,params[param])}),text}export function getCurrentLanguage(){return(window.appI18n||{}).language||document.documentElement.lang||"pl"}export function formatCommentCount(count){return 0===count?t("community.comments.count.zero"):1===count?t("community.comments.count.one"):t("community.comments.count.multiple",{count:count})}export function formatPhotoCount(count){return 0===count?t("community.photos.count.zero"):1===count?t("community.photos.count.one"):t("community.photos.count.multiple",{count:count})}export function formatRatingCount(count){return t(1===count?"community.rating.oneRating":"community.rating.multipleRatings")}