export async function likeComment(commentId,userId){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");if(await hasUserLiked(commentId,userId))return null;const{data:like,error:error}=await sb.from("poi_comment_likes").insert({comment_id:commentId,user_id:userId}).select().single();if(error){if("23505"===error.code)return null;throw error}return like}catch(error){throw console.error("Error liking comment:",error),error}}export async function unlikeComment(commentId,userId){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{error:error}=await sb.from("poi_comment_likes").delete().eq("comment_id",commentId).eq("user_id",userId);if(error)throw error;return!0}catch(error){throw console.error("Error unliking comment:",error),error}}export async function hasUserLiked(commentId,userId){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{count:count,error:error}=await sb.from("poi_comment_likes").select("*",{count:"exact",head:!0}).eq("comment_id",commentId).eq("user_id",userId);if(error)throw error;return(count||0)>0}catch(error){return console.error("Error checking if user liked:",error),!1}}export async function getLikesCount(commentId){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{count:count,error:error}=await sb.from("poi_comment_likes").select("*",{count:"exact",head:!0}).eq("comment_id",commentId);if(error)throw error;return count||0}catch(error){return console.error("Error getting likes count:",error),0}}export async function getCommentLikers(commentId,limit=10){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{data:likes,error:error}=await sb.from("poi_comment_likes").select("user_id, created_at").eq("comment_id",commentId).order("created_at",{ascending:!1}).limit(limit);if(error)throw error;return likes||[]}catch(error){return console.error("Error getting comment likers:",error),[]}}export async function getUserLikedComments(userId,limit=20){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{data:likes,error:error}=await sb.from("poi_comment_likes").select("comment_id, created_at").eq("user_id",userId).order("created_at",{ascending:!1}).limit(limit);if(error)throw error;return likes||[]}catch(error){return console.error("Error getting user liked comments:",error),[]}}export async function getMostLikedComments(poiId,limit=5){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{data:comments,error:commentsError}=await sb.from("poi_comments").select("id, content, user_id, created_at").eq("poi_id",poiId);if(commentsError)throw commentsError;if(!comments||0===comments.length)return[];return(await Promise.all(comments.map(async comment=>{const likesCount=await getLikesCount(comment.id);return{...comment,likesCount:likesCount}}))).sort((a,b)=>b.likesCount-a.likesCount).slice(0,limit)}catch(error){return console.error("Error getting most liked comments:",error),[]}}export async function toggleLike(commentId,userId){try{const liked=await hasUserLiked(commentId,userId);return liked?await unlikeComment(commentId,userId):await likeComment(commentId,userId),{liked:!liked,count:await getLikesCount(commentId)}}catch(error){throw console.error("Error toggling like:",error),error}}export async function getUserLikeStats(userId){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");const{count:likesGiven,error:givenError}=await sb.from("poi_comment_likes").select("*",{count:"exact",head:!0}).eq("user_id",userId);if(givenError)throw givenError;const{data:userComments,error:commentsError}=await sb.from("poi_comments").select("id").eq("user_id",userId);if(commentsError)throw commentsError;const commentIds=userComments?.map(c=>c.id)||[];let likesReceived=0;if(commentIds.length>0){const{count:count,error:receivedError}=await sb.from("poi_comment_likes").select("*",{count:"exact",head:!0}).in("comment_id",commentIds);receivedError||(likesReceived=count||0)}return{likesGiven:likesGiven||0,likesReceived:likesReceived}}catch(error){return console.error("Error getting user like stats:",error),{likesGiven:0,likesReceived:0}}}export function subscribeLikeUpdates(commentId,callback){try{const sb=window.getSupabase();if(!sb)throw new Error("Supabase client not available");return sb.channel(`likes:${commentId}`).on("postgres_changes",{event:"*",schema:"public",table:"poi_comment_likes",filter:`comment_id=eq.${commentId}`},async payload=>{const newCount=await getLikesCount(commentId);callback({event:payload.eventType,count:newCount,payload:payload.new||payload.old})}).subscribe()}catch(error){return console.error("Error subscribing to like updates:",error),null}}