!function(){function initMobileTabbar(){const tabbar=document.querySelector(".mobile-tabbar");tabbar&&tabbar.querySelectorAll(".mobile-tabbar-btn[data-page-url]").forEach(button=>{const pageUrl=button.dataset.pageUrl;pageUrl&&(button.addEventListener("click",e=>{e.preventDefault(),window.location.href=pageUrl}),button.setAttribute("role","link"),button.addEventListener("keydown",e=>{"Enter"!==e.key&&" "!==e.key||(e.preventDefault(),window.location.href=pageUrl)}))})}function setActiveButton(){const tabbar=document.querySelector(".mobile-tabbar");if(!tabbar)return;const currentPage=window.location.pathname.split("/").pop()||"index.html";tabbar.querySelectorAll(".mobile-tabbar-btn").forEach(button=>{const pageUrl=button.dataset.pageUrl,isActive=pageUrl&&pageUrl.includes(currentPage.replace(".html",""));button.classList.toggle("is-active",isActive),button.setAttribute("aria-pressed",isActive?"true":"false")})}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",()=>{initMobileTabbar(),setActiveButton()}):(initMobileTabbar(),setActiveButton())}();