function showPopup(title,message,type){const existing=document.getElementById("bookingPopup");existing&&existing.remove();const overlay=document.createElement("div");overlay.id="bookingPopup",overlay.className=`booking-popup-overlay ${type}`,overlay.innerHTML=`\n    <div class="booking-popup">\n      <div class="booking-popup-icon">\n        ${"success"===type?"✓":"✕"}\n      </div>\n      <h3 class="booking-popup-title">${title}</h3>\n      <p class="booking-popup-message">${message}</p>\n    </div>\n  `,document.body.appendChild(overlay),setTimeout(()=>overlay.classList.add("active"),10),setTimeout(()=>{overlay.classList.remove("active"),setTimeout(()=>overlay.remove(),300)},3e3),overlay.addEventListener("click",()=>{overlay.classList.remove("active"),setTimeout(()=>overlay.remove(),300)})}"undefined"!=typeof window&&(window.showSuccessPopup=function(title,message){showPopup(title,message,"success")},window.showErrorPopup=function(title,message){showPopup(title,message,"error")},window.clearFormValidation=function(form){form&&form.querySelectorAll("input, textarea, select").forEach(input=>{input.setCustomValidity&&input.setCustomValidity(""),input.classList.remove("invalid")})});