export function formatCurrencyEUR(amount){return Number.isFinite(amount)?`€${amount.toFixed(2)}`:"€0.00"}export function formatDistanceKm(meters){return!Number.isFinite(meters)||meters<0?"—":`${(meters/1e3).toFixed(1)} km`}export function calculateXpForLevel(level){return!Number.isFinite(level)||level<1?150:Math.floor(150*Math.pow(1.5,level-1))}export function calculateLevel(xp){if(!Number.isFinite(xp)||xp<0)return 1;let level=1,totalXpNeeded=0;for(;totalXpNeeded+calculateXpForLevel(level)<=xp;)totalXpNeeded+=calculateXpForLevel(level),level++;return level}export function formatAttractionCount(count){if(!Number.isFinite(count)||count<0)return"0 atrakcji";if(1===count)return"1 atrakcja";const remainder100=count%100;if(remainder100>=12&&remainder100<=14)return`${count} atrakcji`;const remainder10=count%10;return remainder10>=2&&remainder10<=4?`${count} atrakcje`:`${count} atrakcji`}