const STORAGE_KEYS={ACCOUNTS:"wakacjecypr-accounts",REVIEWS:"wakacjecypr-reviews",NOTIFICATIONS:"wakacjecypr-notifications",JOURNAL:"wakacjecypr-journal-entries",SELECTED_PLACE:"wakacjecypr-selected-place-redirect"};export function getFromStorage(key,defaultValue=null){try{const item=localStorage.getItem(key);return null===item?defaultValue:JSON.parse(item)}catch(error){return console.error(`Error reading from localStorage (${key}):`,error),defaultValue}}export function setToStorage(key,value){try{return localStorage.setItem(key,JSON.stringify(value)),!0}catch(error){return console.error(`Error writing to localStorage (${key}):`,error),!1}}export function removeFromStorage(key){try{localStorage.removeItem(key)}catch(error){console.error(`Error removing from localStorage (${key}):`,error)}}export function clearAppStorage(){try{Object.values(STORAGE_KEYS).forEach(key=>{localStorage.removeItem(key)})}catch(error){console.error("Error clearing app storage:",error)}}export function isStorageAvailable(){try{const test="__storage_test__";return localStorage.setItem(test,test),localStorage.removeItem(test),!0}catch(error){return!1}}export function getStorageSize(){try{let total=0;for(let key in localStorage)localStorage.hasOwnProperty(key)&&(total+=localStorage[key].length+key.length);return total}catch(error){return 0}}export{STORAGE_KEYS};