export function getTranslation(key,fallback=""){if(window.appI18n&&window.appI18n.translations){const currentLang=window.appI18n.language||"pl";return(window.appI18n.translations[currentLang]||{})[key]||fallback}return fallback}export function translate(key,fallback="",replacements={}){let text=getTranslation(key,fallback);return Object.entries(replacements).forEach(([placeholder,value])=>{const pattern=new RegExp(`\\{\\{\\s*${placeholder}\\s*\\}\\}`,"g");text=text.replace(pattern,String(value))}),text}export function getActiveTranslations(){if("undefined"==typeof window||!window.appI18n)return null;const{language:language="pl",translations:translations={}}=window.appI18n;return translations[language]||null}export function areTranslationsReady(){const translations=getActiveTranslations();return!!(translations&&Object.keys(translations).length>0)}export function getTaskTranslationKey(task,field){return`tasks.${task.id}.${field}`}export function getTaskTitle(task){return getTranslation(getTaskTranslationKey(task,"title"),task.title)}export function getTaskDescription(task){return getTranslation(getTaskTranslationKey(task,"description"),task.description)}export function getPlaceTranslationKey(place,field){return place&&place.id?`places.${place.id}.${field}`:""}export function getPlaceName(place){return getTranslation(getPlaceTranslationKey(place,"name"),place.name||"")}export function getPlaceDescription(place){return getTranslation(getPlaceTranslationKey(place,"description"),place.description||"")}export function getPlaceBadge(place){return getTranslation(getPlaceTranslationKey(place,"badge"),place.badge||"")}