export function normalizeSearchText(value){return String(value||"").toLowerCase().normalize("NFD").replace(/\p{Diacritic}/gu,"")}export function sanitizeAccountProfile(profile){if(!profile||"object"!=typeof profile)return null;const id="string"==typeof profile.id&&profile.id.trim()?profile.id.trim():null,username="string"==typeof profile.username&&profile.username.trim()?profile.username.trim():null,email="string"==typeof profile.email&&profile.email.trim()?profile.email.trim():null;return id?{id:id,username:username,email:email}:null}export function clampRating(rating){const num=Number.isFinite(rating)?Math.floor(rating):0;return Math.max(0,Math.min(5,num))}